import React, { useState, useEffect, useCallback, useRef } from 'react';
import TextField from '@mui/material/TextField';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import { styled } from '@mui/material/styles';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';

function App() {
  const [isSending, setIsSending] = useState(true)
  const [value, setValue] = React.useState(null);
  const [buttonText, setButtonText] = React.useState("Submit");
  const isMounted = useRef(true)
  const Android12Switch = styled(Switch)(({ theme }) => ({
    padding: 8,
    '& .MuiSwitch-track': {
      borderRadius: 22 / 2,
      '&:before, &:after': {
        content: '""',
        position: 'absolute',
        top: '50%',
        transform: 'translateY(-50%)',
        width: 16,
        height: 16,
      },
      '&:before': {
        backgroundImage: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 24 24"><path fill="${encodeURIComponent(
          theme.palette.getContrastText(theme.palette.primary.main),
        )}" d="M21,7L9,19L3.5,13.5L4.91,12.09L9,16.17L19.59,5.59L21,7Z"/></svg>')`,
        left: 12,
      },
      '&:after': {
        backgroundImage: `url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 24 24"><path fill="${encodeURIComponent(
          theme.palette.getContrastText(theme.palette.primary.main),
        )}" d="M19,13H5V11H19V13Z" /></svg>')`,
        right: 12,
      },
    },
    '& .MuiSwitch-thumb': {
      boxShadow: 'none',
      width: 16,
      height: 16,
      margin: 2,
    },
  }));

  // set isMounted to false when we unmount the component
  useEffect(() => {
    return () => {
      isMounted.current = false
    }
  }, [])

  useEffect(() => {
    fetch('/alarmstatus').then(res => res.json()).then(data => {
      if(Boolean(data.status)){
        setIsSending(true);
        setButtonText("Waiting for wake up...")
      }
    });
  }, []);

  const sendTimeRequest = useCallback(async () => {
    // don't send again while we are sending
    if (isSending) return
    // update state
    setIsSending(true)
    //Change button text
    setButtonText("Waiting for wake up...")
    // send the actual request
    await fetch('/setalarmclock', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({time: value})
    }).then(res => res.json()).then(data => {
      console.log(data);
    });

    // once the request is sent, update state again
    if (isMounted.current) // only update if we are still mounted
      setIsSending(false)
  }, [isSending,value]) // update the callback if the state changes

  const sendCancelRequest = useCallback(async () => {
    // don't send again while we are sending
    if (!isSending) return
    //GET to cancel current alarm and set to original state
    fetch('/alarmcancel').then(res => res.json()).then(data => {
      setButtonText("Submit")
      setIsSending(false)
    });

  }, [isSending])

  const sendRoomToggleRequest = useCallback(async () => {
    fetch('/roomcontrol').then(res => res.json()).then(data => {
    });
  }, [])

  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      minHeight="100vh"
    >
      <Grid container
            spacing={1}
            direction="column"
            alignItems="center"
            justifyContent="center"
            >
        <Grid item xs = {12}>
          <LocalizationProvider dateAdapter={AdapterDateFns}>
                <TimePicker
                  label="Wake Up Time: "
                  value={value}
                  onChange={(newValue) => {
                    setValue(newValue);
                  }}
                  renderInput={(params) => <TextField {...params} />}
                />
          </LocalizationProvider>
        </Grid> 
        <Grid item xs = {12}>
          <Button variant="contained" disabled={isSending} onClick={sendTimeRequest}>{buttonText}</Button>  
          <Button variant="contained" disabled={!isSending} onClick={sendCancelRequest}>Cancel</Button>
        </Grid>
        <Grid item xs = {12}>
          <FormControlLabel
            control={<Android12Switch defaultChecked onChange={sendRoomToggleRequest}/>}
            label="Room Switch"
          />
        </Grid>
      </Grid>        
    </Box>
  )
}


export default App;
