import React, { useState, useEffect, useCallback, useRef } from 'react';
import TextField from '@mui/material/TextField';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';

function App() {
  const [isSending, setIsSending] = useState(false)
  const [value, setValue] = React.useState(null);
  const [buttonText, setButtonText] = React.useState("Submit");
  const isMounted = useRef(true)

  // set isMounted to false when we unmount the component
  useEffect(() => {
    return () => {
      isMounted.current = false
    }
  }, [])

  useEffect(() => {
    fetch('/alarmstatus').then(res => res.json()).then(data => {
      if(data.status){
        setIsSending(true)
      }
    });
  }, []);

  const sendRequest = useCallback(async () => {
    // don't send again while we are sending
    if (isSending) return
    // update state
    setIsSending(true)
    //Change button text
    setButtonText("Waiting for wake up...")
    // send the actual request
    await fetch('/setalarmclock', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({time: value})
    }).then(res => res.json()).then(data => {
      console.log(data);
    });

    // once the request is sent, update state again
    if (isMounted.current) // only update if we are still mounted
      setIsSending(false)
  }, [isSending,value]) // update the callback if the state changes

  return (
    <Stack direction="row">

      <LocalizationProvider dateAdapter={AdapterDateFns}>
            <TimePicker
              label="Wake Up Time: "
              value={value}
              onChange={(newValue) => {
                setValue(newValue);
              }}
              renderInput={(params) => <TextField {...params} />}
            />
      </LocalizationProvider>

      <Stack direction="row">
        <Button variant="contained" disabled={isSending} onClick={sendRequest}>{buttonText}</Button>
      </Stack>

    </Stack>

  )
}


export default App;
